import { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom";

function ReservationForm({ setReservation }) {
    const [startTime, setStartTime] = useState("");
    const [minutes, setMinutes] = useState(120);
    const [tableNumber, setTableNumber] = useState("");
    const [availableTables, setAvailableTables] = useState([]);
    const [formError, setFormError] = useState("");

    const navigate = useNavigate();
    const API = useMemo(() => process.env.REACT_APP_API_URL || "http://localhost:8080", []);

    // --- Geschäftsregeln ---
    const MIN_MIN = 30;
    const MAX_MIN = 300;
    const CLOSING_HOUR = 22; // bis 22:00 Uhr erlaubt

    // --- Zeit-Helper ---
    const pad = (n) => (n < 10 ? "0" + n : n);
    const toIsoWithSeconds = (date) =>
        `${date.getFullYear()}-${pad(date.getMonth() + 1)}-${pad(date.getDate())}T${pad(
            date.getHours()
        )}:${pad(date.getMinutes())}:${pad(date.getSeconds())}`;

    const parseLocalDateTime = (value) => (value ? new Date(value + ":00") : null);

    const maxMinutesForStart = (start) => {
        if (!start) return MAX_MIN;
        const latestEnd = new Date(start);
        latestEnd.setHours(CLOSING_HOUR, 0, 0, 0);
        const diffMs = latestEnd.getTime() - start.getTime();
        const diffMin = Math.floor(diffMs / 60000);
        if (diffMin <= 0) return 0;
        return Math.min(MAX_MIN, diffMin);
    };

    // --- Verfügbare Tische abrufen ---
    useEffect(() => {
        const fetchAvailable = async () => {
            setFormError("");
            if (!startTime || !minutes) {
                setAvailableTables([]);
                return;
            }

            const start = parseLocalDateTime(startTime);
            if (!start) return;

            const maxAllowed = maxMinutesForStart(start);
            if (maxAllowed < MIN_MIN) {
                setAvailableTables([]);
                return;
            }

            const startISO = toIsoWithSeconds(start);

            try {
                const res = await fetch(
                    `${API}/api/reservations/available?start=${encodeURIComponent(
                        startISO
                    )}&minutes=${minutes}`,
                    { credentials: "include" }
                );

                if (!res.ok) {
                    let msg = "Fehler beim Laden der verfügbaren Tische.";
                    try {
                        const ct = res.headers.get("content-type") || "";
                        if (ct.includes("application/json")) {
                            const j = await res.json();
                            msg = j?.detail || j?.title || j?.message || msg;
                        } else {
                            const t = await res.text();
                            if (t) msg = t;
                        }
                    } catch {}
                    throw new Error(msg);
                }

                const data = await res.json();
                setAvailableTables(Array.isArray(data) ? data : []);
            } catch (e) {
                console.error(e);
                setFormError(e?.message || "Fehler beim Laden der verfügbaren Tische.");
                setAvailableTables([]);
            }
        };

        fetchAvailable();
    }, [startTime, minutes, API]);

    // --- Tischauswahl aktualisieren ---
    useEffect(() => {
        if (!tableNumber) return;
        const stillAvailable = availableTables.some(
            (t) => String(t.tableNumber) === String(tableNumber)
        );
        if (!stillAvailable) setTableNumber("");
    }, [tableNumber, availableTables]);

    // --- Formular absenden ---
    const handleSubmit = async (e) => {
        e.preventDefault();
        setFormError("");

        const start = parseLocalDateTime(startTime);
        if (!start) return setFormError("Bitte Startzeit auswählen.");
        if (start < new Date())
            return setFormError("Reservierungen in der Vergangenheit sind nicht möglich.");

        if (!minutes || minutes < MIN_MIN || minutes > MAX_MIN)
            return setFormError("Die Dauer muss zwischen 30 und 300 Minuten liegen.");

        const maxAllowed = maxMinutesForStart(start);
        if (minutes > maxAllowed) {
            if (maxAllowed < MIN_MIN) {
                return setFormError(
                    "Die letzte Reservierung muss spätestens um 22:00 Uhr enden. Bitte frühere Zeit wählen."
                );
            }
            return setFormError(
                `Für diese Startzeit ist maximal ${maxAllowed} Minuten erlaubt (bis 22:00 Uhr).`
            );
        }

        if (!tableNumber) return setFormError("Bitte Tisch auswählen.");

        const end = new Date(start.getTime() + minutes * 60 * 1000);

        const payload = {
            tableNumber: Number(tableNumber),
            startTime: toIsoWithSeconds(start),
            endTime: toIsoWithSeconds(end),
        };

        try {
            const resp = await fetch(`${API}/api/reservations`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                credentials: "include",
                body: JSON.stringify(payload),
            });

            if (!resp.ok) {
                let msg = "";
                try {
                    const ct = resp.headers.get("content-type") || "";
                    if (ct.includes("application/json")) {
                        const j = await resp.json();
                        msg = j?.detail || j?.title || j?.message || "";
                        if (!msg && j?.fields && typeof j.fields === "object") {
                            const parts = Object.entries(j.fields).map(
                                ([k, v]) => `${k}: ${String(v)}`
                            );
                            if (parts.length) msg = parts.join(", ");
                        }
                    } else {
                        const t = await resp.text();
                        if (t) msg = t;
                    }
                } catch {}
                if (!msg) {
                    switch (resp.status) {
                        case 400:
                        case 422:
                            msg = "Eingaben sind ungültig. Bitte prüfen.";
                            break;
                        case 401:
                            msg = "Nicht autorisiert.";
                            break;
                        case 409:
                            msg = "Zeitfenster oder Tisch ist bereits belegt.";
                            break;
                        case 500:
                            msg = "Serverfehler. Bitte später erneut versuchen.";
                            break;
                        default:
                            msg = "Reservierung fehlgeschlagen – bitte erneut versuchen.";
                    }
                }
                throw new Error(msg);
            }

            const data = await resp.json();
            setReservation([data]);
            navigate("/reservations/my");
        } catch (err) {
            console.error(err);
            setFormError(err?.message ? err.message : "Reservierung fehlgeschlagen.");
        }
    };

    // --- Mindestzeit für datetime-local ---
    const nowLocalForMin = useMemo(() => {
        const d = new Date();
        d.setSeconds(0, 0);
        return `${d.getFullYear()}-${pad(d.getMonth() + 1)}-${pad(d.getDate())}T${pad(
            d.getHours()
        )}:${pad(d.getMinutes())}`;
    }, []);

    // --- Optionen für Dauer ---
    const renderDurationOptions = () => {
        const start = parseLocalDateTime(startTime);
        const maxAllowed = maxMinutesForStart(start);
        const items = [];
        for (let m = MIN_MIN; m <= MAX_MIN; m += 30) {
            const disabled = start && m > maxAllowed;
            const label =
                m < 60
                    ? `${m} Min`
                    : `${Math.floor(m / 60)} Std${m % 60 ? ` ${m % 60} Min` : ""}`;
            items.push(
                <option key={m} value={m} disabled={disabled}>
                    {label}
                    {disabled ? " (bis 22:00 Uhr)" : ""}
                </option>
            );
        }
        return items;
    };

    const start = parseLocalDateTime(startTime);
    const maxAllowedForHint = maxMinutesForStart(start);
    const endPreview =
        start && minutes ? new Date(start.getTime() + minutes * 60000) : null;

    return (
        <form onSubmit={handleSubmit} className="reservation-form" noValidate>
            {/* Startzeit */}
            <label htmlFor="startTime" style={{ display: "block", marginBottom: 4 }}>
                Beginn der Reservierung
            </label>
            <input
                id="startTime"
                type="datetime-local"
                value={startTime}
                min={nowLocalForMin}
                onChange={(e) => {
                    setStartTime(e.target.value);
                    setFormError("");
                }}
                required
            />
            <small style={{ display: "block", marginTop: 4, color: "#555" }}>
                Reservierungen sind nur bis <strong>22:00 Uhr</strong> am selben Tag möglich.
            </small>

            {/* Dauer */}
            <label htmlFor="duration" style={{ display: "block", margin: "12px 0 4px" }}>
                Dauer
            </label>
            <select
                id="duration"
                value={minutes}
                onChange={(e) => {
                    setMinutes(Number(e.target.value));
                    setFormError("");
                }}
                required
            >
                {renderDurationOptions()}
            </select>

            {/* Tischauswahl */}
            <label htmlFor="tableNumber" style={{ display: "block", margin: "12px 0 4px" }}>
                Tisch
            </label>
            <select
                id="tableNumber"
                value={tableNumber}
                onChange={(e) => setTableNumber(e.target.value)}
                required
                disabled={maxAllowedForHint < MIN_MIN}
            >
                <option value="">Tisch auswählen…</option>
                {availableTables.map((t) => (
                    <option key={t.id} value={t.tableNumber}>
                        Tisch {t.tableNumber} ({t.numberOfSeats} Pers.)
                    </option>
                ))}
            </select>

            {/* Ende-Vorschau */}
            {endPreview && (
                <small style={{ display: "block", marginTop: 6, color: "#555" }}>
                    Ende:{" "}
                    {endPreview.toLocaleTimeString("de-DE", {
                        hour: "2-digit",
                        minute: "2-digit",
                    })}{" "}
                    (spätestens 22:00 Uhr)
                </small>
            )}

            {/* Fehlermeldungen */}
            {formError && (
                <p role="alert" data-cy="form-error" style={{ color: "#c62828", marginTop: 10 }}>
                    {formError}
                </p>
            )}

            <button
                type="submit"
                style={{ marginTop: 12 }}
                disabled={maxAllowedForHint < MIN_MIN}
            >
                Reservieren
            </button>
        </form>
    );
}

export default ReservationForm;
