// src/components/ReservationForm.jsx
import { useMemo, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import TimePicker from "./TimePicker";

/** ---- Lokalne helpery (bez osobnych plików) ---- */
const pad = (n) => (n < 10 ? `0${n}` : `${n}`);
const toIsoWithSeconds = (d) =>
    `${d.getFullYear()}-${pad(d.getMonth() + 1)}-${pad(d.getDate())}T${pad(
        d.getHours()
    )}:${pad(d.getMinutes())}:${pad(d.getSeconds())}`;
const buildStart = (dateStr, hh, mm) =>
    dateStr && hh != null && mm != null
        ? new Date(`${dateStr}T${pad(+hh)}:${pad(+mm)}:00`)
        : null;
const maxMinutesForStart = (start, closeHour, maxMin) => {
    if (!start) return maxMin;
    const latestEnd = new Date(start);
    latestEnd.setHours(closeHour, 0, 0, 0);
    const diffMin = Math.floor((latestEnd - start) / 60000);
    if (diffMin <= 0) return 0;
    return Math.min(maxMin, diffMin);
};

/** ---- „Wewnętrzny hook” w tym samym pliku ---- */
function useAvailableTables({ api, start, minutes }) {
    const [tables, setTables] = useState([]);
    const [loading, setLoading] = useState(false);
    const [err, setErr] = useState("");

    useEffect(() => {
        if (!start || !minutes) {
            setTables([]);
            return;
        }
        const ctrl = new AbortController();
        const t = setTimeout(async () => {
            setLoading(true);
            setErr("");
            try {
                const iso = toIsoWithSeconds(start);
                const res = await fetch(
                    `${api}/api/reservations/available?start=${encodeURIComponent(
                        iso
                    )}&minutes=${minutes}`,
                    { credentials: "include", signal: ctrl.signal }
                );
                if (!res.ok) throw new Error("Fehler beim Laden der verfügbaren Tische.");
                const data = await res.json();
                setTables(Array.isArray(data) ? data : []);
            } catch (e) {
                if (e.name !== "AbortError") setErr(e.message || "Fehler beim Laden.");
            } finally {
                setLoading(false);
            }
        }, 250);
        return () => {
            clearTimeout(t);
            ctrl.abort();
        };
    }, [api, start?.getTime(), minutes]);

    return { tables, loading, error: err };
}

/** ---- Główna forma ---- */
export default function ReservationForm({ setReservation }) {
    // stałe biznesowe
    const MIN_MIN = 30;
    const MAX_MIN = 300;
    const OPEN_HOUR = 12;
    const CLOSE_HOUR = 22; // lokal jest czynny do 22:00 -> ostatnia godzina startu = 21:30

    const navigate = useNavigate();
    const API = useMemo(
        () => process.env.REACT_APP_API_URL || "http://localhost:8080",
        []
    );

    // stan
    const [dateStr, setDateStr] = useState(() => {
        const d = new Date();
        return d.toISOString().slice(0, 10); // YYYY-MM-DD
    });
    const [hm, setHM] = useState({ hour: "18", minute: "00" }); // steruje TimePickerem
    const [duration, setDuration] = useState(120);
    const [tableNumber, setTableNumber] = useState("");
    const [formError, setFormError] = useState("");

    // po zmianie godziny 22 wymuszamy minuty 00 (tu i tak 22 nie będzie do wyboru, ale zostawiamy safeguard)
    useEffect(() => {
        if (Number(hm.hour) >= CLOSE_HOUR && hm.minute !== "00") {
            setHM((p) => ({ ...p, minute: "00" }));
        }
    }, [hm.hour]); // eslint-disable-line

    // wyliczenia
    const start = useMemo(
        () => buildStart(dateStr, hm.hour, hm.minute),
        [dateStr, hm]
    );
    const maxAllowed = useMemo(
        () => maxMinutesForStart(start, CLOSE_HOUR, MAX_MIN),
        [start]
    );
    const end = useMemo(
        () => (start ? new Date(start.getTime() + duration * 60000) : null),
        [start, duration]
    );

    // dostępne stoliki
    const { tables: availableTables, loading: availLoading, error: availError } =
        useAvailableTables({ api: API, start, minutes: duration });

    // jeśli wybrany stolik zniknie z listy — reset
    useEffect(() => {
        if (!tableNumber) return;
        if (!availableTables.some((t) => String(t.tableNumber) === String(tableNumber))) {
            setTableNumber("");
        }
    }, [availableTables, tableNumber]);

    // walidacja
    function validate() {
        if (!start) return "Bitte Startzeit auswählen.";
        if (start < new Date())
            return "Reservierungen in der Vergangenheit sind nicht möglich.";
        if (duration < MIN_MIN || duration > MAX_MIN)
            return "Die Dauer muss zwischen 30 und 300 Minuten liegen.";
        if (maxAllowed < MIN_MIN)
            return "Die letzte Reservierung muss spätestens um 22:00 Uhr enden. Bitte frühere Zeit wählen.";
        if (duration > maxAllowed)
            return `Für diese Startzeit ist maximal ${maxAllowed} Minuten erlaubt (bis 22:00 Uhr).`;
        if (!tableNumber) return "Bitte Tisch auswählen.";
        return null;
    }

    async function handleSubmit(e) {
        e.preventDefault();
        setFormError("");
        const err = validate();
        if (err) return setFormError(err);

        const payload = {
            tableNumber: Number(tableNumber),
            startTime: toIsoWithSeconds(start),
            endTime: toIsoWithSeconds(end),
        };

        try {
            const resp = await fetch(`${API}/api/reservations`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                credentials: "include",
                body: JSON.stringify(payload),
            });
            const txt = await resp.text();
            if (!resp.ok) throw new Error(txt || "Reservierung fehlgeschlagen.");

            const data = txt ? JSON.parse(txt) : null;
            setReservation([data]);
            navigate("/reservations/my");
        } catch (e) {
            setFormError(`Reservierung fehlgeschlagen: ${e.message}`);
        }
    }

    // min dla date (dziś)
    const todayStr = useMemo(() => new Date().toISOString().slice(0, 10), []);

    return (
        <form onSubmit={handleSubmit} className="reservation-form" noValidate>
            {/* Data */}
            <label htmlFor="date" style={{ display: "block", marginBottom: 4 }}>
                Datum
            </label>
            <input
                id="date"
                type="date"
                value={dateStr}
                min={todayStr}
                onChange={(e) => {
                    setDateStr(e.target.value);
                    setFormError("");
                }}
                required
            />

            {/* Godzina (TimePicker) — tylko 12–21, minuty 00/30 */}
            <label style={{ display: "block", margin: "12px 0 4px" }}>Uhrzeit</label>
            <TimePicker
                hour={hm.hour}
                minute={hm.minute}
                minHour={OPEN_HOUR}
                maxHour={CLOSE_HOUR - 1} // 21 – brak 22
                stepMinutes={30}
                onChange={(next) => {
                    setHM(next);
                    setFormError("");
                }}
            />
            <small style={{ display: "block", marginTop: 4, color: "#555" }}>
                Schritte: 30 Minuten, letzte Startzeit <strong>21:30</strong> (Ende spätestens 22:00).
            </small>

            {/* Hidden dla Cypress */}
            <input
                id="startTime"
                type="hidden"
                value={start ? `${dateStr}T${pad(+hm.hour)}:${pad(+hm.minute)}` : ""}
                readOnly
            />

            {/* Czas trwania */}
            <label htmlFor="duration" style={{ display: "block", margin: "12px 0 4px" }}>
                Dauer
            </label>
            <select
                id="duration"
                value={duration}
                onChange={(e) => {
                    setDuration(Number(e.target.value));
                    setFormError("");
                }}
                required
            >
                {Array.from({ length: (MAX_MIN - MIN_MIN) / 30 + 1 }, (_, i) => {
                    const m = MIN_MIN + i * 30;
                    const disabled = start && m > maxAllowed;
                    const label =
                        m < 60
                            ? `${m} Min`
                            : `${Math.floor(m / 60)} Std${m % 60 ? ` ${m % 60} Min` : ""}`;
                    return (
                        <option key={m} value={m} disabled={disabled}>
                            {label}
                            {disabled ? " (bis 22:00 Uhr)" : ""}
                        </option>
                    );
                })}
            </select>

            {/* Stoliki */}
            <label htmlFor="tableNumber" style={{ display: "block", margin: "12px 0 4px" }}>
                Tisch
            </label>
            <select
                id="tableNumber"
                value={tableNumber}
                onChange={(e) => setTableNumber(e.target.value)}
                required
                disabled={maxAllowed < MIN_MIN || availLoading}
            >
                <option value="">
                    {availLoading ? "Lade…" : "Tisch auswählen…"}
                </option>
                {availableTables.map((t) => (
                    <option key={t.id} value={t.tableNumber}>
                        Tisch {t.tableNumber} ({t.numberOfSeats} Pers.)
                    </option>
                ))}
            </select>
            {availError && (
                <small style={{ color: "#b71c1c" }}>{availError}</small>
            )}

            {/* Podgląd końca */}
            {end && (
                <small style={{ display: "block", marginTop: 6, color: "#555" }}>
                    Ende:{" "}
                    {end.toLocaleTimeString("de-DE", { hour: "2-digit", minute: "2-digit" })}{" "}
                    (spätestens 22:00 Uhr)
                </small>
            )}

            {/* Błąd */}
            {formError && (
                <p data-cy="form-error" style={{ color: "#c62828", marginTop: 10 }}>
                    {formError}
                </p>
            )}

            <button type="submit" style={{ marginTop: 12 }} disabled={maxAllowed < MIN_MIN}>
                Reservieren
            </button>
        </form>
    );
}
