// src/components/ReservationForm.jsx
import { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function ReservationForm({ setReservation }) {
    // stałe
    const OPEN_HOUR = 12;
    const CLOSE_HOUR = 22; // lokal czynny do 22:00 → start tylko 12..21
    const MIN_MIN = 30;
    const MAX_MIN = 300;

    const navigate = useNavigate();
    const API = useMemo(
        () => process.env.REACT_APP_API_URL || "http://localhost:8080",
        []
    );

    // stan
    const [dateStr, setDateStr] = useState(() => new Date().toISOString().slice(0, 10));
    const [hour, setHour] = useState(18);       // number (strzałki)
    const [minute, setMinute] = useState("00"); // "00" | "30"
    const [duration, setDuration] = useState(120);
    const [tableNumber, setTableNumber] = useState("");
    const [availableTables, setAvailableTables] = useState([]);
    const [loadingTables, setLoadingTables] = useState(false);
    const [formError, setFormError] = useState("");
    const [fetchKey, setFetchKey] = useState("");

    // helpers lokalnie (bez nowych plików)
    const pad = (n) => (n < 10 ? `0${n}` : `${n}`);
    const toIsoWithSeconds = (d) =>
        `${d.getFullYear()}-${pad(d.getMonth() + 1)}-${pad(d.getDate())}T${pad(
            d.getHours()
        )}:${pad(d.getMinutes())}:${pad(d.getSeconds())}`;

    const start = useMemo(() => {
        if (!dateStr) return null;
        return new Date(`${dateStr}T${pad(hour)}:${minute}:00`);
    }, [dateStr, hour, minute]);

    const maxAllowed = useMemo(() => {
        if (!start) return MAX_MIN;
        const latestEnd = new Date(start);
        latestEnd.setHours(CLOSE_HOUR, 0, 0, 0);
        const diff = Math.floor((latestEnd - start) / 60000);
        if (diff <= 0) return 0;
        return Math.min(diff, MAX_MIN);
    }, [start]);

    const end = useMemo(
        () => (start ? new Date(start.getTime() + duration * 60000) : null),
        [start, duration]
    );

    // bezpieczeństwo: nie pozwól na 22 jako start
    useEffect(() => {
        if (hour >= CLOSE_HOUR) setHour(CLOSE_HOUR - 1);
    }, [hour]);

    // prosty debounce pobierania
    useEffect(() => {
        const t = setTimeout(() => {
            if (start && duration) setFetchKey(`${start.getTime()}|${duration}`);
        }, 350);
        return () => clearTimeout(t);
    }, [start?.getTime(), duration]);

    useEffect(() => {
        if (!fetchKey) return;
        if (maxAllowed < MIN_MIN) {
            setAvailableTables([]);
            return;
        }
        const controller = new AbortController();
        (async () => {
            setLoadingTables(true);
            try {
                const res = await fetch(
                    `${API}/api/reservations/available?start=${encodeURIComponent(
                        toIsoWithSeconds(start)
                    )}&minutes=${duration}`,
                    { credentials: "include", signal: controller.signal }
                );
                const json = res.ok ? await res.json() : [];
                setAvailableTables(Array.isArray(json) ? json : []);
            } catch {
                setAvailableTables([]);
            } finally {
                setLoadingTables(false);
            }
        })();
        return () => controller.abort();
    }, [API, fetchKey, maxAllowed, duration, start]);

    // jeśli wybrany stolik wypadnie – reset
    useEffect(() => {
        if (
            tableNumber &&
            !availableTables.some((t) => String(t.tableNumber) === String(tableNumber))
        ) {
            setTableNumber("");
        }
    }, [availableTables, tableNumber]);

    // walidacja
    function validate() {
        if (!start) return "Bitte Startzeit auswählen.";
        if (start < new Date())
            return "Reservierungen in der Vergangenheit sind nicht möglich.";
        if (duration < MIN_MIN || duration > MAX_MIN)
            return "Die Dauer muss zwischen 30 und 300 Minuten liegen.";
        if (maxAllowed < MIN_MIN)
            return "Die letzte Reservierung muss spätestens um 22:00 Uhr enden. Bitte frühere Zeit wählen.";
        if (duration > maxAllowed)
            return `Für diese Startzeit ist maximal ${maxAllowed} Minuten erlaubt (bis 22:00 Uhr).`;
        if (!tableNumber) return "Bitte Tisch auswählen.";
        return null;
    }

    async function handleSubmit(e) {
        e.preventDefault();
        setFormError("");
        const err = validate();
        if (err) return setFormError(err);

        const resp = await fetch(`${API}/api/reservations`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            credentials: "include",
            body: JSON.stringify({
                tableNumber: Number(tableNumber),
                startTime: toIsoWithSeconds(start),
                endTime: toIsoWithSeconds(end),
            }),
        });

        const text = await resp.text();
        if (!resp.ok) return setFormError(text || "Reservierung fehlgeschlagen.");

        const data = text ? JSON.parse(text) : null;
        setReservation([data]);
        navigate("/reservations/my");
    }

    const todayStr = useMemo(() => new Date().toISOString().slice(0, 10), []);

    return (
        <form onSubmit={handleSubmit} className="reservation-form" noValidate>
            {/* DATA */}
            <div className="form-row">
                <label htmlFor="date">Datum</label>
                <input
                    id="date"
                    type="date"
                    value={dateStr}
                    min={todayStr}
                    onChange={(e) => {
                        setDateStr(e.target.value);
                        setFormError("");
                    }}
                    required
                />
            </div>

            {/* GODZINA */}
            <div className="form-row">
                <label>Uhrzeit</label>
                <div className="time-inline">
                    <input
                        id="hour"
                        type="number"
                        min={OPEN_HOUR}
                        max={CLOSE_HOUR - 1} // 21
                        step={1}
                        value={hour}
                        onChange={(e) =>
                            setHour(
                                Math.max(
                                    OPEN_HOUR,
                                    Math.min(CLOSE_HOUR - 1, Number(e.target.value) || OPEN_HOUR)
                                )
                            )
                        }
                        aria-label="Stunde"
                        required
                    />
                    <span className="time-sep">:</span>
                    <select
                        id="minute"
                        value={minute}
                        onChange={(e) => setMinute(e.target.value)}
                        aria-label="Minute"
                    >
                        <option value="00">00</option>
                        <option value="30">30</option>
                    </select>
                </div>
                <small className="hint">
                    Schritte: 30 Minuten, letzte Startzeit <strong>21:30</strong> (Ende spätestens 22:00).
                </small>
            </div>

            {/* Hidden dla Cypress */}
            <input
                id="startTime"
                type="hidden"
                value={start ? `${dateStr}T${pad(hour)}:${minute}` : ""}
                readOnly
            />

            {/* CZAS TRWANIA */}
            <div className="form-row">
                <label htmlFor="duration">Dauer</label>
                <select
                    id="duration"
                    value={duration}
                    onChange={(e) => setDuration(Number(e.target.value))}
                    required
                >
                    {Array.from({ length: (MAX_MIN - MIN_MIN) / 30 + 1 }, (_, i) => {
                        const m = MIN_MIN + i * 30; // 30, 60, 90…
                        const disabled = start && m > maxAllowed;
                        const label =
                            m < 60
                                ? `${m} Min`
                                : `${Math.floor(m / 60)} Std${m % 60 ? ` ${m % 60} Min` : ""}`;
                        return (
                            <option key={m} value={m} disabled={disabled}>
                                {label}
                                {disabled ? " (bis 22:00 Uhr)" : ""}
                            </option>
                        );
                    })}
                </select>
            </div>

            {/* STOLIK */}
            <div className="form-row">
                <label htmlFor="tableNumber">Tisch</label>
                <select
                    id="tableNumber"
                    value={tableNumber}
                    onChange={(e) => setTableNumber(e.target.value)}
                    required
                    disabled={loadingTables || maxAllowed < MIN_MIN}
                >
                    <option value="">{loadingTables ? "Lade…" : "Tisch auswählen…"}</option>
                    {availableTables.map((t) => (
                        <option key={t.id} value={t.tableNumber}>
                            Tisch {t.tableNumber} ({t.numberOfSeats} Pers.)
                        </option>
                    ))}
                </select>
            </div>

            {/* PODGLĄD KOŃCA */}
            {end && (
                <div className="form-row">
                    <small className="hint">
                        Ende:{" "}
                        {end.toLocaleTimeString("de-DE", { hour: "2-digit", minute: "2-digit" })}{" "}
                        (spätestens 22:00 Uhr)
                    </small>
                </div>
            )}

            {/* BŁĄD */}
            {formError && (
                <p data-cy="form-error" className="form-error">
                    {formError}
                </p>
            )}

            <div className="form-row">
                <button type="submit" disabled={maxAllowed < MIN_MIN}>
                    Reservieren
                </button>
            </div>
        </form>
    );
}
