import "../styles/loginAndRegister.css";
import { Link, useNavigate } from "react-router-dom";
import { useMemo, useState } from "react";

function Register() {
    const [fullName, setFullName] = useState("");
    const [email, setEmail] = useState("");
    const [phone, setPhone] = useState("");
    const [password, setPassword] = useState("");

    const [showPwd, setShowPwd] = useState(false);
    const [submitting, setSubmitting] = useState(false);
    const [errorMsg, setErrorMsg] = useState("");
    const [successMsg, setSuccessMsg] = useState("");

    const navigate = useNavigate();
    const API = useMemo(
        () => process.env.REACT_APP_API_URL || "http://localhost:8080",
        []
    );

    const validEmail = (v) => /\S+@\S+\.\S+/.test(v);
    const validPhone = (v) => !v || /^(\+?\d{1,3}[\s-]?)?\d{6,15}$/.test(v.trim());
    async function handleSubmit(e) {
        e.preventDefault();
        setErrorMsg("");
        setSubmitting(true);

        try {
            const res = await fetch(`${API}/auth/login`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                credentials: "include",
                body: JSON.stringify({ email, password }),
            });

            if (!res.ok) {
                let msg = "";
                try {
                    // spróbuj odczytać komunikat z backendu (text/plain lub json.message)
                    const raw = await res.text();
                    if (raw) {
                        try {
                            const j = JSON.parse(raw);
                            msg = (j && j.message) || raw;
                        } catch {
                            msg = raw;
                        }
                    }
                } catch {
                    /* ignoruj problemy z odczytem body */
                }

                if (!msg) {
                    // fallback po statusie
                    switch (res.status) {
                        case 401:
                            msg = "E-Mail oder Passwort ist falsch.";
                            break;
                        case 429:
                            msg = "Zu viele Versuche. Bitte später erneut versuchen.";
                            break;
                        case 500:
                            msg = "Serverfehler. Bitte später erneut versuchen.";
                            break;
                        default:
                            msg = "Login fehlgeschlagen.";
                    }
                }
                throw new Error(msg);
            }

            let data;
            try {
                data = await res.json();
            } catch {
                throw new Error("Unerwartete Serverantwort.");
            }

            localStorage.setItem(
                "authUser",
                JSON.stringify({
                    username: data.username, // u Ciebie to e-mail
                    role: data.role,
                    fullName: data.fullName,
                    email: data.email,
                    phone: data.phone,
                })
            );

            if (data.role === "ROLE_ADMIN") navigate("/admin");
            else navigate("/myaccount");
        } catch (err) {
            setErrorMsg(err?.message || "Ein Fehler ist aufgetreten.");
        } finally {
            setSubmitting(false);
        }
    }

    const isDisabled =
        submitting ||
        fullName.trim().length < 2 ||
        !validEmail(email) ||
        password.length < 6;

    return (
        <main className="auth-page" aria-label="Registrierung">
            <section className="auth-card" aria-labelledby="register-title">
                <h1 id="register-title" className="auth-title">Registrieren</h1>

                {errorMsg && <div className="auth-alert" role="alert">{errorMsg}</div>}
                {successMsg && <div className="auth-success" role="status">{successMsg}</div>}

                <form className="auth-form" onSubmit={handleSubmit} noValidate>
                    <div className="field">
                        <label htmlFor="fullName">Vollständiger Name</label>
                        <input
                            id="fullName"
                            type="text"
                            autoComplete="name"
                            value={fullName}
                            onChange={(e) => setFullName(e.target.value)}
                            minLength={2}
                            required
                        />
                        <small className="hint">Mindestens 2 Zeichen</small>
                    </div>

                    <div className="field">
                        <label htmlFor="email">E-Mail</label>
                        <input
                            id="email"
                            type="email"
                            autoComplete="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                        <small className="hint">Bitte gültige Adresse z. B. name@mail.de</small>
                    </div>

                    <div className="field">
                        <label htmlFor="phone">Telefon (optional)</label>
                        <input
                            id="phone"
                            type="tel"
                            autoComplete="tel"
                            value={phone}
                            onChange={(e) => setPhone(e.target.value)}
                            placeholder="+49 170 1234567"
                        />
                        <small className="hint">Nur Zahlen, +, - und Leerzeichen erlaubt</small>
                    </div>

                    <div className="field">
                        <label htmlFor="password">Passwort</label>
                        <div className="pwd-wrap">
                            <input
                                id="password"
                                type={showPwd ? "text" : "password"}
                                autoComplete="new-password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                minLength={6}
                                required
                            />
                            <button
                                type="button"
                                className="pwd-toggle"
                                onClick={() => setShowPwd((v) => !v)}
                                aria-label={showPwd ? "Passwort verbergen" : "Passwort anzeigen"}
                            >
                                {showPwd ? "Verbergen" : "Anzeigen"}
                            </button>
                        </div>
                        <small className="hint">Mindestens 6 Zeichen</small>
                    </div>

                    <button className="auth-btn" type="submit" disabled={isDisabled}>
                        {submitting ? <span className="spinner" aria-hidden="true" /> : null}
                        {submitting ? "Wird registriert…" : "Registrieren"}
                    </button>
                </form>

                <p className="auth-meta">
                    Bereits ein Konto?{" "}
                    <Link to="/login" className="auth-link">Jetzt einloggen</Link>
                </p>
            </section>
        </main>
    );
}

export default Register;
