import React, { useState } from "react";

export default function TimePicker({ value, onChange }) {
    const [time, setTime] = useState(value || "18:00");

    const handleChange = (e) => {
        const val = e.target.value;

        // Walidacja: tylko HH:mm i tylko minuty 00 lub 30
        const match = /^([01]\d|2[0-3]):(00|30)$/.test(val);
        if (match) {
            setTime(val);
            onChange(val);
        } else {
            setTime(val); // pozwól wpisać, ale nie aktualizuj onChange
        }
    };

    const adjustTime = (minutes) => {
        const [h, m] = time.split(":").map(Number);
        let total = h * 60 + m + minutes;
        if (total < 0) total = 0;
        if (total >= 24 * 60) total = 0;
        const newH = Math.floor(total / 60);
        const newM = total % 60 === 30 ? "30" : "00";
        const formatted = `${String(newH).padStart(2, "0")}:${newM}`;
        setTime(formatted);
        onChange(formatted);
    };

    return (
        <div style={{ display: "flex", alignItems: "center", gap: "8px" }}>
            <input
                type="text"
                value={time}
                onChange={handleChange}
                pattern="([01]\d|2[0-3]):(00|30)"
                title="Bitte Zeit im Format HH:mm eingeben (z. B. 18:00 oder 18:30)"
                style={{ width: "70px", textAlign: "center" }}
            />
            <button type="button" onClick={() => adjustTime(30)}>▲</button>
            <button type="button" onClick={() => adjustTime(-30)}>▼</button>
        </div>
    );
}
