import { useEffect, useState } from "react";
import { Navigate } from "react-router-dom";

/** Basis-URL des Backends (kann über ENV-Variable gesetzt werden). */
const API = import.meta.env.VITE_API_URL ?? "http://localhost:8080";

/**
 * Wrapper-Komponente zur Prüfung, ob der Benutzer bereits eingeloggt ist.
 *
 * <p>Wird verwendet, um z. B. den Zugriff auf Login- oder Register-Seiten zu verhindern,
 * wenn der Benutzer bereits authentifiziert ist. Führt in diesem Fall eine automatische
 * Weiterleitung zur Seite <code>/myaccount</code> durch.</p>
 *
 * <ul>
 *   <li>Führt bei der Initialisierung eine Anfrage an <code>/auth/auth_check</code> aus.</li>
 *   <li>Verwendet das JWT-Cookie (<code>credentials: "include"</code>).</li>
 *   <li>Zeigt während der Prüfung eine kurze Ladeanzeige an.</li>
 *   <li>Rendert das übergebene Kind-Element nur, wenn keine aktive Session vorhanden ist.</li>
 * </ul>
 *
 * @component
 * @param {Object} props - React-Props.
 * @param {JSX.Element} props.children - Das zu rendernde Kind-Element (z. B. Login-Formular).
 * @returns {JSX.Element} - Weiterleitung oder übergebene Kinder.
 */
export default function IsLoggedCheck({ children }) {
    /** Zustand: ob der Benutzer bereits eingeloggt ist. */
    const [isLogged, setIsLogged] = useState(false);

    /** Zustand: ob gerade die Prüfung läuft. */
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        /** Asynchrone Prüfung der Benutzer-Authentifizierung. */
        const checkAuth = async () => {
            try {
                const res = await fetch(`${API}/auth/auth_check`, {
                    method: "GET",
                    credentials: "include", // JWT-Cookie wird mitgeschickt
                });

                if (res.ok) {
                    setIsLogged(true); // Benutzer ist eingeloggt
                } else {
                    setIsLogged(false); // Benutzer ist nicht eingeloggt
                }
            } catch {
                // Netzwerkausfall oder Serverfehler → Benutzer als ausgeloggt behandeln
                setIsLogged(false);
            } finally {
                setIsLoading(false); // Prüfung abgeschlossen
            }
        };

        checkAuth();
    }, []);

    /** Während die Authentifizierung geprüft wird. */
    if (isLoading) {
        return <p>Überprüfung der Anmeldung...</p>;
    }

    /** Wenn Benutzer eingeloggt ist → weiterleiten. */
    if (isLogged) {
        return <Navigate to="/myaccount" replace />;
    }

    /** Wenn Benutzer nicht eingeloggt ist → Kinder rendern. */
    return children;
}
