import React, { useState } from "react";
import TimePicker from "./TimePicker"; // import naszego komponentu
import axios from "axios";

export default function ReservationForm() {
    const [date, setDate] = useState("");
    const [startTime, setStartTime] = useState("18:00");
    const [endTime, setEndTime] = useState("20:00");
    const [message, setMessage] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!date || !startTime || !endTime) {
            setMessage("Bitte Datum und Uhrzeit auswählen.");
            return;
        }

        const startDateTime = `${date}T${startTime}:00`;
        const endDateTime = `${date}T${endTime}:00`;

        try {
            const response = await axios.post(
                "/api/reservations",
                {
                    tableNumber: 1, // przykładowy stolik
                    startTime: startDateTime,
                    endTime: endDateTime,
                },
                { withCredentials: true }
            );
            setMessage(`Reservierung erfolgreich! Tisch ${response.data.tableNumber}`);
        } catch (error) {
            setMessage("Fehler bei der Reservierung.");
            console.error(error);
        }
    };

    return (
        <form
            onSubmit={handleSubmit}
            style={{
                display: "flex",
                flexDirection: "column",
                maxWidth: "400px",
                margin: "20px auto",
                gap: "12px",
                background: "#f7f7f7",
                padding: "20px",
                borderRadius: "10px",
                boxShadow: "0 0 10px rgba(0,0,0,0.1)",
            }}
        >
            <h2 style={{ textAlign: "center" }}>Neue Reservierung</h2>

            <label>
                Datum:
                <input
                    type="date"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    required
                />
            </label>

            <label>
                Startzeit:
                <TimePicker value={startTime} onChange={setStartTime} />
            </label>

            <label>
                Endzeit:
                <TimePicker value={endTime} onChange={setEndTime} />
            </label>

            <button
                type="submit"
                style={{
                    backgroundColor: "#2b7a78",
                    color: "white",
                    padding: "10px",
                    border: "none",
                    borderRadius: "5px",
                    cursor: "pointer",
                }}
            >
                Reservieren
            </button>

            {message && (
                <p
                    style={{
                        textAlign: "center",
                        color: message.includes("Fehler") ? "red" : "green",
                    }}
                >
                    {message}
                </p>
            )}
        </form>
    );
}
