import { Link, useLocation } from "react-router-dom";
import { useEffect, useState } from "react";
import "../styles/header.css";

/**
 * Navigationskomponente der Webanwendung.
 *
 * <p>Die Header-Komponente zeigt das Hauptmenü der Restaurant-Reservierungs-App.
 * Sie überprüft automatisch den aktuellen Login-Status und die Benutzerrolle,
 * um dynamisch die passenden Navigationslinks anzuzeigen.</p>
 *
 * <ul>
 *   <li>Bei nicht eingeloggten Benutzer:innen werden die Links <em>Register</em> und <em>Login</em> angezeigt.</li>
 *   <li>Bei eingeloggten Benutzer:innen erscheint der Link <em>My Account</em>.</li>
 *   <li>Bei Administrator:innen wird zusätzlich der Link <em>Admin Panel</em> angezeigt.</li>
 * </ul>
 *
 * <p>Die Komponente sendet bei jeder Routenänderung eine Anfrage an das Backend-Endpunkt
 * <code>/auth/auth_check</code>, um den Authentifizierungsstatus über das JWT-Cookie zu prüfen.</p>
 *
 * @component
 * @returns {JSX.Element} Der Navigations-Header mit dynamischen Links je nach Benutzerstatus
 */
function Header() {
    const location = useLocation(); // Zugriff auf den aktuellen Pfad (Route)
    const [isLogged, setIsLogged] = useState(false); // Zustand: Benutzer eingeloggt?
    const [role, setRole] = useState(null); // Zustand: Benutzerrolle (USER / ADMIN)

    /**
     * Überprüft bei jeder Routenänderung, ob der Benutzer eingeloggt ist.
     *
     * <p>Sendet eine Anfrage an <code>http://localhost:8080/auth/auth_check</code>.
     * Wenn das JWT im Cookie gültig ist, werden Login-Status und Rolle im State gespeichert.</p>
     *
     * @async
     * @returns {Promise<void>} Aktualisiert die Zustände {@link isLogged} und {@link role}.
     */
    useEffect(() => {
        const check = async () => {
            try {
                const res = await fetch("http://localhost:8080/auth/auth_check", {
                    credentials: "include", // JWT-Cookie wird mitgesendet
                });

                if (res.ok) {
                    const data = await res.json();
                    setIsLogged(true);
                    setRole(data.role);
                } else {
                    setIsLogged(false);
                    setRole(null);
                }
            } catch {
                setIsLogged(false);
                setRole(null);
            }
        };

        check();
    }, [location.pathname]); // Bei jeder Pfadänderung erneut prüfen

    return (
        <header className="navbar">
            <div className="navbar-container">
                {/* Logo / Markenname */}
                <div className="navbar-logo">
                    <Link to="/">Restaurant</Link>
                </div>

                {/* Navigationslinks */}
                <nav className="navbar-links">
                    <Link to="/" className={location.pathname === "/" ? "active" : ""}>Home</Link>

                    {/* Links für nicht eingeloggte Benutzer */}
                    {!isLogged && (
                        <>
                            <Link to="/register" className={location.pathname === "/register" ? "active" : ""}>
                                Register
                            </Link>
                            <Link to="/login" className={location.pathname === "/login" ? "active" : ""}>
                                Login
                            </Link>
                        </>
                    )}

                    {/* Link für eingeloggte Benutzer (keine Admins) */}
                    {isLogged && role !== "ROLE_ADMIN" && (
                        <Link to="/myaccount" className={location.pathname === "/myaccount" ? "active" : ""}>
                            My Account
                        </Link>
                    )}

                    {/* Link für Administrator:innen */}
                    {isLogged && role === "ROLE_ADMIN" && (
                        <Link to="/admin" className={location.pathname === "/admin" ? "active" : ""}>
                            Admin Panel
                        </Link>
                    )}
                </nav>
            </div>
        </header>
    );
}

export default Header;
