// src/components/ReservationForm.jsx
import { useState, useEffect, useMemo, useRef } from "react";
import { useNavigate } from "react-router-dom";

function ReservationForm({ setReservation }) {
    /** ------------------ STATES & BASISKONSTANTEN ------------------ */
    const [startDatePart, setStartDatePart] = useState("");  // Datum (YYYY-MM-DD)
    const [hour, setHour] = useState("12");                  // Stunde
    const [minute, setMinute] = useState("00");              // Minute
    const [minutes, setMinutes] = useState(120);             // Dauer (in Minuten)
    const [tableNumber, setTableNumber] = useState("");      // Ausgew√§hlter Tisch
    const [availableTables, setAvailableTables] = useState([]); // Verf√ºgbare Tische
    const [formError, setFormError] = useState("");          // Fehlermeldung im Formular

    const navigate = useNavigate();
    const API = useMemo(
        () => process.env.REACT_APP_API_URL || "http://localhost:8080",
        []
    );

    /** ------------------ GESCH√ÑFTSREGELN ------------------ */
    const MIN_MIN = 30;          // Minimale Reservierungsdauer
    const MAX_MIN = 300;         // Maximale Reservierungsdauer
    const OPENING_HOUR = 12;     // √ñffnungszeit
    const LAST_SELECTABLE_HOUR = 21; // Letzte ausw√§hlbare Stunde (21:30 -> Ende 22:00)

    /** ------------------ HILFSFUNKTIONEN ------------------ */
    const pad = (n) => (n < 10 ? "0" + n : "" + n); // Null auff√ºllen
    const todayDateStr = useMemo(() => {
        const d = new Date();
        return `${d.getFullYear()}-${pad(d.getMonth() + 1)}-${pad(d.getDate())}`;
    }, []);

    const toIsoWithSeconds = (date) =>
        `${date.getFullYear()}-${pad(date.getMonth() + 1)}-${pad(
            date.getDate()
        )}T${pad(date.getHours())}:${pad(date.getMinutes())}:${pad(
            date.getSeconds()
        )}`;

    // Kombination aus Datum + Uhrzeit zu vollst√§ndigem ISO-String
    const startTimePart = `${pad(Number(hour))}:${pad(Number(minute))}`;
    const buildStartDate = (date, hh, mm) =>
        date ? new Date(`${date}T${pad(Number(hh))}:${pad(Number(mm))}:00`) : null;

    const startDateObj = buildStartDate(startDatePart, hour, minute);

    /** ------------------ MAXIMALE ZEITBERECHNUNG ------------------ */
        // Berechnet, wie lange man maximal bleiben darf (bis 22:00 Uhr)
    const maxMinutesForStart = (start) => {
            if (!start) return MAX_MIN;
            const latestEnd = new Date(start);
            latestEnd.setHours(22, 0, 0, 0);
            const diffMin = Math.floor((latestEnd - start) / 60000);
            if (diffMin <= 0) return 0;
            return Math.min(MAX_MIN, diffMin);
        };

    const maxAllowedForHint = maxMinutesForStart(startDateObj);
    const endPreview =
        startDateObj && minutes
            ? new Date(startDateObj.getTime() + minutes * 60000)
            : null;

    /** ------------------ INITIALISIERUNG ------------------ */
    // Automatische Vorauswahl: Datum = heute, Zeit = n√§chste halbe Stunde
    useEffect(() => {
        const now = new Date();
        const todayStr = `${now.getFullYear()}-${pad(now.getMonth() + 1)}-${pad(
            now.getDate()
        )}`;
        if (!startDatePart) setStartDatePart(todayStr);

        let h = now.getHours();
        let m = now.getMinutes() < 30 ? 30 : 0;
        if (m === 0 && now.getMinutes() >= 30) h++;

        // Vor √ñffnung ‚Üí 12:00, nach 21:30 ‚Üí n√§chster Tag 12:00
        if (h < OPENING_HOUR) {
            h = OPENING_HOUR;
            m = 0;
        } else if (h > LAST_SELECTABLE_HOUR || (h === LAST_SELECTABLE_HOUR && m > 30)) {
            const t = new Date(now);
            t.setDate(now.getDate() + 1);
            const nextStr = `${t.getFullYear()}-${pad(t.getMonth() + 1)}-${pad(
                t.getDate()
            )}`;
            setStartDatePart(nextStr);
            h = OPENING_HOUR;
            m = 0;
        }

        setHour(String(h));
        setMinute(pad(m));
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, []);

    /** ------------------ VERF√úGBARE TISCHE LADEN ------------------ */
        // Optimiert mit Debounce (300 ms) + AbortController, damit keine Spam-Requests entstehen
    const lastQueryRef = useRef(""); // Merkt sich die letzte API-Abfrage (startISO|minutes)
    useEffect(() => {


        // Randf√§lle: kein Datum oder Dauer
        if (!startDateObj || !minutes) {
            setAvailableTables([]);
            return;
        }
        const h = startDateObj.getHours();
        const mm = startDateObj.getMinutes();

        // Nur erlaubte Zeiten abfragen (12:00‚Äì21:30)
        if (h < OPENING_HOUR || h > LAST_SELECTABLE_HOUR || (h === LAST_SELECTABLE_HOUR && mm > 30)) {
            setAvailableTables([]);
            return;
        }

        const maxAllowed = maxMinutesForStart(startDateObj);
        if (maxAllowed < MIN_MIN) {
            setAvailableTables([]);
            return;
        }

        const startISO = toIsoWithSeconds(startDateObj);
        const key = `${startISO}|${minutes}`;
        if (key === lastQueryRef.current) {
            // Nichts ge√§ndert ‚Äì keine neue Abfrage
            return;
        }

        const ctrl = new AbortController();
        const t = setTimeout(async () => {
            try {
                const res = await fetch(
                    `${API}/api/reservations/available?start=${encodeURIComponent(
                        startISO
                    )}&minutes=${minutes}`,
                    { credentials: "include", signal: ctrl.signal }
                );
                if (!res.ok) throw new Error("Fehler beim Laden der verf√ºgbaren Tische.");
                const data = await res.json();
                setAvailableTables(Array.isArray(data) ? data : []);
                lastQueryRef.current = key; // Merke: erfolgreich geladen
            } catch (e) {
                if (e.name !== "AbortError") {
                    console.error(e);
                    setAvailableTables([]);
                }
            }
        }, 300); // Debounce-Zeit

        return () => {
            clearTimeout(t);
            ctrl.abort();
        };
    }, [API, minutes, startDateObj]);

    /** ------------------ RESET, FALLS TISCH NICHT MEHR VERF√úGBAR ------------------ */
    useEffect(() => {
        if (!tableNumber) return;
        const stillAvail = availableTables.some(
            (t) => String(t.tableNumber) === String(tableNumber)
        );
        if (!stillAvail) setTableNumber("");
    }, [availableTables, tableNumber]);

    /** ------------------ VALIDIERUNG UND ABSENDEN ------------------ */
    const handleSubmit = async (e) => {
        e.preventDefault();
        setFormError("");

        // --- walidacje jak dotƒÖd ---
        if (!startDateObj) return setFormError("Bitte Startzeit ausw√§hlen.");
        const h = startDateObj.getHours();
        const mm = startDateObj.getMinutes();

        if (
            h < OPENING_HOUR ||
            h > LAST_SELECTABLE_HOUR ||
            (h === LAST_SELECTABLE_HOUR && mm > 30)
        ) {
            return setFormError(
                "Reservierungen sind nur zwischen 12:00 und 21:30 (Ende bis 22:00) m√∂glich."
            );
        }
        if (startDateObj < new Date()) {
            return setFormError("Reservierungen in der Vergangenheit sind nicht m√∂glich.");
        }
        if (!minutes || minutes < MIN_MIN || minutes > MAX_MIN) {
            return setFormError("Die Dauer muss zwischen 30 und 300 Minuten liegen.");
        }

        const maxAllowed = maxMinutesForStart(startDateObj);
        if (minutes > maxAllowed) {
            if (maxAllowed < MIN_MIN) {
                return setFormError(
                    "Die letzte Reservierung muss sp√§testens um 22:00 Uhr enden. Bitte fr√ºhere Zeit w√§hlen."
                );
            }
            return setFormError(
                `F√ºr diese Startzeit ist maximal ${maxAllowed} Minuten erlaubt (bis 22:00 Uhr).`
            );
        }
        if (!tableNumber) return setFormError("Bitte Tisch ausw√§hlen.");

        const end = new Date(startDateObj.getTime() + minutes * 60000);
        const payload = {
            tableNumber: Number(tableNumber),
            startTime: toIsoWithSeconds(startDateObj),
            endTime: toIsoWithSeconds(end),
        };

        try {
            const resp = await fetch(`${API}/api/reservations`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                credentials: "include",
                body: JSON.stringify(payload),
            });

            // ---- 409 KONFLIKT (stolik ju≈º zajƒôty) ‚Üí poka≈º tre≈õƒá z backendu ----
            if (resp.status === 409) {
                const ct = resp.headers.get("content-type") || "";
                let msg = "Tisch ist bereits reserviert.";
                try {
                    if (ct.includes("application/json")) {
                        // kompatybilnie z globalnym handlerem (ProblemDetail itp.)
                        const j = await resp.json();
                        msg = j.detail || j.title || JSON.stringify(j);
                    } else {
                        msg = (await resp.text()) || msg;
                    }
                } catch (_) {}
                setFormError(msg);
                return; // nie nawigujemy
            }

            // ---- inne b≈Çƒôdy 4xx/5xx ‚Üí poka≈º sensowny komunikat ----
            if (!resp.ok) {
                const ct = resp.headers.get("content-type") || "";
                let msg = "";
                try {
                    msg = ct.includes("application/json")
                        ? JSON.stringify(await resp.json())
                        : await resp.text();
                } catch (_) {}
                throw new Error(msg || `HTTP ${resp.status}`);
            }

            // ---- sukces 2xx ‚Üí parsuj payload i nawiguj ----
            const ct = resp.headers.get("content-type") || "";
            const data = ct.includes("application/json")
                ? await resp.json()
                : JSON.parse(await resp.text());
            setReservation([data]);
            navigate("/reservations/my");
        } catch (err) {
            setFormError(`Reservierung fehlgeschlagen: ${err.message || err}`);
        }
    };
    /** ------------------ FORMULAR ------------------ */
    return (
        <form onSubmit={handleSubmit} className="reservation-form" noValidate>
            {/* Datum */}
            <label htmlFor="startDate" style={{ display: "block", marginBottom: 4 }}>
                Datum
            </label>
            <input
                id="startDate"
                type="date"
                value={startDatePart}
                min={todayDateStr}
                onChange={(e) => {
                    setStartDatePart(e.target.value);
                    setFormError("");
                    lastQueryRef.current = "";
                }}
                required
            />

            {/* Uhrzeit */}
            <label style={{ display: "block", margin: "12px 0 4px" }}>Uhrzeit</label>
            <div style={{ display: "flex", gap: 8 }}>
                <select
                    id="hour"
                    style={{ width: "40%" }}   // üîπ schmalere Felder
                    value={hour}
                    onChange={(e) => {
                        setHour(e.target.value);
                        setFormError("");
                        lastQueryRef.current = "";
                    }}
                    aria-label="Stunde"
                >
                    {Array.from(
                        { length: LAST_SELECTABLE_HOUR - OPENING_HOUR + 1 },
                        (_, i) => OPENING_HOUR + i
                    ).map((h) => (
                        <option key={h} value={String(h)}>
                            {pad(h)}
                        </option>
                    ))}
                </select>

                <select
                    id="minute"
                    style={{ width: "40%" }}   // üîπ schmalere Felder
                    value={minute}
                    onChange={(e) => {
                        setMinute(e.target.value);
                        setFormError("");
                        lastQueryRef.current = "";
                    }}
                    aria-label="Minute"
                >
                    <option value="00">00</option>
                    <option value="30">30</option>
                </select>
            </div>
            <small style={{ display: "block", marginTop: 4, color: "#555" }}>
                Start nur von <strong>12:00</strong> bis <strong>21:30</strong> (Ende sp√§testens 22:00).
            </small>

            {/* Hidden #startTime (f√ºr Cypress-Tests) */}
            <input
                id="startTime"
                type="hidden"
                value={startDatePart ? `${startDatePart}T${startTimePart}` : ""}
                readOnly
            />

            {/* Dauer */}
            <label htmlFor="duration" style={{ display: "block", margin: "12px 0 4px" }}>
                Dauer
            </label>
            <select
                id="duration"
                value={minutes}
                onChange={(e) => {
                    setMinutes(Number(e.target.value));
                    setFormError("");
                    lastQueryRef.current = "";
                }}
                required
            >
                {Array.from({ length: (MAX_MIN - MIN_MIN) / 30 + 1 }, (_, i) => {
                    const m = MIN_MIN + i * 30;
                    const disabled = !!startDateObj && m > maxMinutesForStart(startDateObj);
                    const label =
                        m < 60
                            ? `${m} Min`
                            : `${Math.floor(m / 60)} Std${m % 60 ? ` ${m % 60} Min` : ""}`;
                    return (
                        <option key={m} value={m} disabled={disabled}>
                            {label}
                            {disabled ? " (bis 22:00 Uhr)" : ""}
                        </option>
                    );
                })}
            </select>

            {/* Tisch */}
            <label htmlFor="tableNumber" style={{ display: "block", margin: "12px 0 4px" }}>
                Tisch
            </label>
            <select
                id="tableNumber"
                value={tableNumber}
                onChange={(e) => setTableNumber(e.target.value)}
                required
                disabled={maxAllowedForHint < MIN_MIN}
            >
                <option value="">Tisch ausw√§hlen‚Ä¶</option>
                {availableTables.map((t) => (
                    <option key={t.id} value={t.tableNumber}>
                        Tisch {t.tableNumber} ({t.numberOfSeats} Pers.)
                    </option>
                ))}
            </select>

            {/* Endzeit-Vorschau */}
            {endPreview && (
                <small style={{ display: "block", marginTop: 6, color: "#555" }}>
                    Ende:{" "}
                    {endPreview.toLocaleTimeString("de-DE", { hour: "2-digit", minute: "2-digit" })}{" "}
                    (sp√§testens 22:00 Uhr)
                </small>
            )}

            {/* Fehlerausgabe */}
            {formError && (
                <p data-cy="form-error" style={{ color: "#c62828", marginTop: 10 }}>
                    {formError}
                </p>
            )}

            {/* Absenden */}
            <button type="submit" style={{ marginTop: 12 }} disabled={maxAllowedForHint < MIN_MIN}>
                Reservieren
            </button>
        </form>
    );
}

export default ReservationForm;
