import { useState, useEffect, useMemo, useRef } from "react";
import { useNavigate } from "react-router-dom";

function ReservationForm({ setReservation }) {
    /** ---------- CONSTANTS & HELPERS ---------- */
    const API = useMemo(() => process.env.REACT_APP_API_URL || "http://localhost:8080", []);
    const navigate = useNavigate();

    const MIN_MIN = 30;
    const MAX_MIN = 300;
    const OPENING_HOUR = 12;
    const LAST_SELECTABLE_HOUR = 21;

    const pad = (n) => (n < 10 ? `0${n}` : `${n}`);
    const todayDateStr = useMemo(() => {
        const d = new Date();
        return `${d.getFullYear()}-${pad(d.getMonth() + 1)}-${pad(d.getDate())}`;
    }, []);

    const toIsoWithSeconds = (date) =>
        `${date.getFullYear()}-${pad(date.getMonth() + 1)}-${pad(date.getDate())}T${pad(date.getHours())}:${pad(date.getMinutes())}:${pad(date.getSeconds())}`;

    const buildStartDate = (date, hh, mm) =>
        date ? new Date(`${date}T${pad(Number(hh))}:${pad(Number(mm))}:00`) : null;

    const adjustTime = () => {
        const now = new Date();
        const adjusted = { date: todayDateStr, hour: now.getHours(), minute: now.getMinutes() < 30 ? '30' : '00' };
        if (adjusted.minute === '00' && now.getMinutes() >= 30) adjusted.hour++;
        if (adjusted.hour < OPENING_HOUR) adjusted.hour = OPENING_HOUR;
        if (adjusted.hour > LAST_SELECTABLE_HOUR || (adjusted.hour === LAST_SELECTABLE_HOUR && adjusted.minute > '30')) {
            now.setDate(now.getDate() + 1);
            adjusted.date = `${now.getFullYear()}-${pad(now.getMonth() + 1)}-${pad(now.getDate())}`;
            adjusted.hour = OPENING_HOUR;
            adjusted.minute = '00';
        }
        return adjusted;
    };

    const maxMinutesForStart = (start) => {
        if (!start) return MAX_MIN;
        const latestEnd = new Date(start);
        latestEnd.setHours(22, 0, 0, 0);
        return Math.max(0, Math.min(MAX_MIN, Math.floor((latestEnd - start) / 60000)));
    };

    /** ---------- STATES ---------- */
    const [startDatePart, setStartDatePart] = useState("");
    const [hour, setHour] = useState("12");
    const [minute, setMinute] = useState("00");
    const [minutes, setMinutes] = useState(120);
    const [tableNumber, setTableNumber] = useState("");
    const [availableTables, setAvailableTables] = useState([]);
    const [formError, setFormError] = useState("");

    const lastQueryRef = useRef("");

    const startDateObj = buildStartDate(startDatePart, hour, minute);
    const maxAllowedForHint = maxMinutesForStart(startDateObj);
    const endPreview = startDateObj ? new Date(startDateObj.getTime() + minutes * 60000) : null;

    /** ---------- INITIALIZE DEFAULT STATE ---------- */
    useEffect(() => {
        const { date, hour, minute } = adjustTime();
        setStartDatePart(date);
        setHour(String(hour));
        setMinute(minute);
    }, []);

    /** ---------- FETCH AVAILABLE TABLES (WITH DEBOUNCE) ---------- */
    useEffect(() => {
        if (!startDateObj || !minutes || maxAllowedForHint < MIN_MIN) {
            setAvailableTables([]);
            return;
        }

        const startISO = toIsoWithSeconds(startDateObj);
        const key = `${startISO}|${minutes}`;
        if (key === lastQueryRef.current) return;

        const ctrl = new AbortController();
        const timeout = setTimeout(async () => {
            try {
                const res = await fetch(
                    `${API}/api/reservations/available?start=${encodeURIComponent(startISO)}&minutes=${minutes}`,
                    { credentials: "include", signal: ctrl.signal }
                );
                if (!res.ok) throw new Error("Fehler beim Laden der verfügbaren Tische.");
                const data = await res.json();
                setAvailableTables(Array.isArray(data) ? data : []);
            } catch (err) {
                if (err.name !== "AbortError") setAvailableTables([]);
            }
        }, 300);

        lastQueryRef.current = key;
        return () => {
            clearTimeout(timeout);
            ctrl.abort();
        };
    }, [API, minutes, startDateObj, maxAllowedForHint]);

    /** ---------- REMOVE INVALID TABLE SELECTION ---------- */
    useEffect(() => {
        if (tableNumber && !availableTables.some((t) => String(t.tableNumber) === String(tableNumber))) {
            setTableNumber("");
        }
    }, [availableTables, tableNumber]);

    /** ---------- FORM SUBMISSION ---------- */
    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!startDateObj) return setFormError("Bitte Startzeit auswählen.");
        if (!tableNumber) return setFormError("Bitte Tisch auswählen.");
        if (minutes < MIN_MIN || minutes > MAX_MIN || minutes > maxAllowedForHint) {
            return setFormError(`Reservierungen müssen zwischen ${MIN_MIN} und ${maxAllowedForHint} Minuten liegen.`);
        }

        try {
            const payload = {
                tableNumber: Number(tableNumber),
                startTime: toIsoWithSeconds(startDateObj),
                endTime: toIsoWithSeconds(new Date(startDateObj.getTime() + minutes * 60000)),
            };
            const res = await fetch(`${API}/api/reservations`, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                credentials: "include",
                body: JSON.stringify(payload),
            });

            if (res.status === 409) {
                const conflictMessage = await res.json();
                return setFormError(conflictMessage.detail || "Reservierungskonflikt.");
            }
            if (!res.ok) throw new Error(`Fehler: ${res.status}`);

            const data = await res.json();
            setReservation(data);
            navigate("/reservations/my");
        } catch (err) {
            setFormError(err.message || "Reservierung fehlgeschlagen.");
        }
    };

    /** ---------- FORM RENDER ---------- */
    return (
        <form onSubmit={handleSubmit} className="reservation-form" noValidate>
            <label htmlFor="startDate">Datum</label>
            <input
                id="startDate"
                type="date"
                value={startDatePart}
                min={todayDateStr}
                onChange={(e) => {
                    setStartDatePart(e.target.value);
                    setFormError("");
                    lastQueryRef.current = "";
                }}
                required
            />

            <label>Uhrzeit</label>
            <div>
                <select
                    value={hour}
                    onChange={(e) => {
                        setHour(e.target.value);
                        setFormError("");
                        lastQueryRef.current = "";
                    }}
                >
                    {Array.from({ length: LAST_SELECTABLE_HOUR - OPENING_HOUR + 1 }).map((_, i) => {
                        const h = OPENING_HOUR + i;
                        return <option key={h} value={h}>{pad(h)}</option>;
                    })}
                </select>
                <select
                    value={minute}
                    onChange={(e) => {
                        setMinute(e.target.value);
                        setFormError("");
                        lastQueryRef.current = "";
                    }}
                >
                    <option value="00">00</option>
                    <option value="30">30</option>
                </select>
            </div>

            <label htmlFor="duration">Dauer</label>
            <select
                id="duration"
                value={minutes}
                onChange={(e) => {
                    setMinutes(Number(e.target.value));
                    setFormError("");
                    lastQueryRef.current = "";
                }}
                required
            >
                {Array.from({ length: (MAX_MIN - MIN_MIN) / 30 + 1 }).map((_, i) => {
                    const m = MIN_MIN + i * 30;
                    return (
                        <option key={m} value={m} disabled={m > maxAllowedForHint}>
                            {m < 60 ? `${m} Min` : `${Math.floor(m / 60)} Std ${m % 60 ? `${m % 60} Min` : ""}`}
                        </option>
                    );
                })}
            </select>

            <label htmlFor="tableNumber">Tisch</label>
            <select
                id="tableNumber"
                value={tableNumber}
                onChange={(e) => setTableNumber(e.target.value)}
                required
                disabled={maxAllowedForHint < MIN_MIN}
            >
                <option value="">Tisch auswählen…</option>
                {availableTables.map((t) => (
                    <option key={t.id} value={t.tableNumber}>
                        Tisch {t.tableNumber} ({t.numberOfSeats} Pers.)
                    </option>
                ))}
            </select>

            {endPreview && <span>Ende: {endPreview.toLocaleTimeString("de-DE", { hour: "2-digit", minute: "2-digit" })}</span>}

            {formError && (
                <p data-cy="form-error" style={{ color: "red" }}>
                    {formError}
                </p>
            )}

            <button type="submit" disabled={maxAllowedForHint < MIN_MIN}>Reservieren</button>
        </form>
    );
}

export default ReservationForm;
